mapper.update:
update teacher set
teachername=#{teachername} , image=#{image} , sex=#{sex}
,
birthday=#{birthday} , contents=#{contents} where
teacherid=#{teacherid}

mapper.select:
select a.* from teacher a where 1=1

	and a.teachername = #{teachername}


	and a.image = #{image}


	and a.sex = #{sex}


	and a.birthday = #{birthday}


	and a.contents = #{contents}
