mapper.insert:
insert into bbs(bbsid , usersid , title , contents , addtime , hits , repnum
) values(#{bbsid} , #{usersid} , #{title} , #{contents} , #{addtime} ,
#{hits} , #{repnum} )

mapper.update:
update bbs set usersid=#{usersid} , title=#{title} , contents=#{contents} ,
addtime=#{addtime} , hits=#{hits} , repnum=#{repnum} where
bbsid=#{bbsid}

mapper.select:
select a.* , b.username , b.image from bbs a , users b where a.usersid
= b.usersid

	and a.usersid = #{usersid}


	and a.title = #{title}


	and a.contents = #{contents}


	and a.addtime = #{addtime}


	and a.hits = #{hits}


	and a.repnum = #{repnum}
